/* * Click nbfs://nbhost/SystemFileSystem/Templates/Licenses/license-default.txt to change this license * Click nbfs://nbhost/SystemFileSystem/Templates/Classes/Class.java to edit this template */package model;import java.util.Date;/** * Category Model - Represents a product category in the warehouse system * Maps to Categories table in WarehouseDB * * @author lengo */public class Category {    private int categoryId;    private String name;    private String description;    private boolean status;    private Date createdAt;    private Date updatedAt;    public Category() {    }    public Category(int categoryId, String name, String description, boolean status, Date createdAt, Date updatedAt) {        this.categoryId = categoryId;        this.name = name;        this.description = description;        this.status = status;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public int getCategoryId() {        return categoryId;    }    public void setCategoryId(int categoryId) {        this.categoryId = categoryId;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public boolean isStatus() {        return status;    }    public void setStatus(boolean status) {        this.status = status;    }    public Date getCreatedAt() {        return createdAt;    }    public void setCreatedAt(Date createdAt) {        this.createdAt = createdAt;    }    public Date getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Date updatedAt) {        this.updatedAt = updatedAt;    }    @Override    public String toString() {        return "Category{" +                "categoryId=" + categoryId +                ", name='" + name + '\'' +                ", description='" + description + '\'' +                ", status=" + status +                ", createdAt=" + createdAt +                ", updatedAt=" + updatedAt +                '}';    }}